sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   jslog('NeedItUtils');
    // Instantiate the GetEmailAddress Script Include 
    var needItUtils = new GlideAjax('NeedItUtils');
    // Specify the getEmail method
    needItUtils.addParam('sysparm_name', 'asyncIsDatePast');
    // Pass the Requested for sys_id
    needItUtils.addParam('sysparm_requestDate', g_form.getValue('u_when_needed'));
    // Send the request to the server
    needItUtils.getXML(checkDateCallback);


    // When the response is back from the server
    function checkDateCallback(response) {
        // Extract the email address from the response, clear any value from the email field, 
        // set new value in the email field
        var passed = response.responseXML.documentElement.getAttribute("answer");
        if(!passed){
			//alert("When needed date cannot be in the past.  pls check and try again.");
			g_form.addErrorMessage("When needed date cannot be in the past.  pls check and try again.");
		}
    }
}
